import React from 'react'

const API= "http://localhost:3000/api/v1/cocktails"

export default class Form extends React.Component{

    //creating proportion class actually 

    // cocktail: name, description, instructions, source
    //ingredient: name
    // proportions: amount, cocktail, ingredient
    state={
        name:"",
        desc:"",
        instructions: "",
        source: "",
        cocktail:{},
        ingredient:"",
        amount:""
    }

    handleChange=(event)=>{
        this.setState({
            [event.target.id]: event.target.value
        })
    }

    handleSubmit=(event)=>{
        event.preventDefault()
        let cocktailObj = {
            name: this.state.name,
            desc: this.state.desc,
            instructions: this.state.instruction,
            source: this.state.source
        }

        this.setState({
            cocktail: cocktailObj
        })




        fetch(API,{
            method: "POST",
            body:data
        })




    }

    render()
    {
        return(
            <React.Fragment>
                <h1 style={{paddingBottom:"5%"}}>Create a Cocktail</h1>
                    <form>
                        <div className="form-group">
                            <label htmlFor="name">Name</label>
                            <input type="text" onChange={this.handleChange} className="form-control" name= "name" value={this.state.name}/> 
                        </div>
                        <div className="form-group">
                            <label htmlFor="description">Description</label>
                            <input type="text" onChange={this.handleChange} className="form-control" id="desc" value={this.state.desc}/>

                        </div>
                        <div className="form-group">
                            <label htmlFor="instructions">Instructions</label>
                            <input type="text" onChange={this.handleChange} className="form-control" id="instructions" value={this.state.instructions}/>

                        </div>
                        <div className="form-group">
                            <label htmlFor="source">Source</label>
                            <input type="text" onChange={this.handleChange} className="form-control" id="source" value={this.state.source}/>
                        </div>

                        <div className="form-group">
                            <label htmlFor="ingredient">Ingredient</label>
                            <input type="text" onChange={this.handleChange} className="form-control" id="ingredient" value={this.state.ingredient}/>

                            <div className="form-group">
                            <label htmlFor="proportion">Proportions</label>
                            <input type="text" onChange={this.handleChange} className="form-control" id="proportion" value={this.state.amount}/>
                        </div>
                        </div>
                            <button type="text" className="btn btn-primary">Submit</button>

                </form>
            </React.Fragment>
        )
    }
}
