import React from 'react'
import './index.css';
import Cocktail from '../components/Cocktail'



export default class CocktailIndexContainer extends React.Component{

    createCocktail=(cocktail)=>{
        if(cocktail){
            return (
                <div className="cocktail" onClick={()=>this.props.handleClickedCocktail(cocktail)} >
                    <h4>{cocktail.name}</h4>
                </div>
            )
        }
        return null

    }

    render(){

        return(
            <div className="container-fluid scrollbar-primary" id="index-container">
                <div className="force-overflow">
               {
                 this.props.cocktails.map(cocktail=>{
                    return <Cocktail key={cocktail.name} cocktail={cocktail} onClick={this.props.handleClickedCocktail}/>
                   })
               }
               </div>
            </div>

        )
    }

}
