import React from 'react'
import './index.css';
import Cocktail from '../components/Cocktail'



export default class CocktailIndexContainer extends React.Component{

    state ={
        search: ""
    }

    handleChange=(event)=>{
        this.setState({
            [event.target.name]: event.target.value
        })
    }

    render(){
        let newArray = this.props.cocktails.filter(cocktailObj => {
            return  cocktailObj.name === this.state.search
        })


        return(
            <div className="container-fluid scrollbar-primary" id="index-container">
                <input name="search" onChange={this.handleChange} value={this.state.search}></input>

                <div className="force-overflow">
               {
                   newArray.map(cocktailObj=>{
                       return <Cocktail key={cocktailObj.name} cocktail={cocktailObj}/>
                   })
               }
               </div>
            </div>

        )
    }

}
